/**
 * Accessibility and Special Cases
 * High contrast, reduced motion, and print styles
 */

/* High contrast mode support */
@media (prefers-contrast: more) {
  .button-primary,
  .button-danger {
    border: 2px solid currentcolor;
  }

  .player-card {
    border: 2px solid rgb(255 255 255 / 50%);
  }

  .die {
    border-width: 3px;
  }

  .army-button {
    border: 2px solid currentcolor;
  }

  .player-select-button {
    border: 2px solid rgb(255 255 255 / 50%);
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  * {
    transition-duration: 0.01ms !important;
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
  }


  .player-card::before {
    display: none;
  }

  .army-button::before {
    display: none;
  }

  .animate-spin,
  .animate-pulse,
  .animate-bounce {
    animation: none;
  }
}

/* Focus styles for keyboard navigation */
*:focus {
  outline: none;
}

.button:focus,
.player-select-button:focus,
.army-button:focus {
  outline: 3px solid rgb(74 93 58 / 50%);
  outline-offset: 2px;
}

/* Screen reader only content */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  white-space: nowrap;
  border: 0;
  clip: rect(0, 0, 0, 0);
}

/* Print styles */
@media print {
  .header,
  .button-container,
  .modal-overlay,
  .screen-footer {
    display: none;
  }

  .app {
    color: #000;
    background: #fff;
  }

  .player-card,
  .die {
    border: 2px solid #000 !important;
    box-shadow: none !important;
  }

  .screen-content {
    padding: 0;
  }

  .battle-screen {
    padding: 0;
  }

  .dice-display {
    gap: 1rem;
  }

  .player-info {
    margin-bottom: 1rem;
  }
}

/* Dark mode support (if implemented) */
@media (prefers-color-scheme: dark) {
  :root {
    --color-text: #fff;
    --color-text-light: #d1d5db;
    --color-background: #1f2937;
    --color-surface: #374151;
    --background-overlay: rgb(0 0 0 / 30%);
  }
}
