/**
 * Battle Screen Component Styles
 * Dice battle interface with player cards, dice display, and controls
 */

/* Battle screen layout */
.battle-screen {
  display: grid;
  grid-template-areas:
    "player-cards"
    "dice-area"
    "controls";
  grid-template-rows: min-content 1fr min-content;
  gap: 0;
  width: 100%;
  height: 100%;
  padding: var(--spacing-md);
}

/* Player info cards */
.player-info {
  display: flex;
  grid-area: player-cards;
  gap: var(--spacing-sm);
  align-self: start;
  justify-content: center;
  width: 100%;
  padding: var(--spacing-xs);
}

.player-card {
  position: relative;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  align-items: center;
  padding: 1.5rem;
  overflow: hidden;
  color: #fff;
  text-align: center;
  cursor: pointer;
  border-radius: var(--border-radius-lg);
  box-shadow: 0 4px 12px rgb(0 0 0 / 15%);
  backdrop-filter: blur(1px);
  transition: all var(--transition-slow);
}

.player-card:hover {
  box-shadow: 0 8px 24px rgb(0 0 0 / 20%);
  transform: translateY(-4px);
}

.player-card:active {
  transform: translateY(-2px);
}

.player-card::before {
  position: absolute;
  inset: 0;
  content: "";
  background: linear-gradient(45deg, transparent 30%, rgb(255 255 255 / 10%) 50%, transparent 70%);
  transform: translateX(-100%);
  transition: transform 0.6s ease;
}

.player-card:hover::before {
  transform: translateX(100%);
}

.player-info .player-card {
  flex: 1;
  max-width: 12rem;
  padding: 0.75rem;
  text-align: center;
}

.player-name {
  font-size: 1.25rem;
  font-weight: bold;
  text-shadow: 0 1px 3px rgb(0 0 0 / 30%);
}

.army-count {
  display: flex;
  gap: 0.25rem;
  align-items: baseline;
  font-size: 1.5rem;
  font-weight: bold;
}

.current-armies {
  font-size: 2rem;
}

.total-armies {
  font-size: 1rem;
  opacity: 0.8;
}

/* Dice container */
.dice-container {
  display: flex;
  flex-direction: column;
  grid-area: dice-area;
  gap: var(--spacing-md);
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  min-height: 0;
  padding: 0;
  margin: 0;
  font: inherit;
  color: inherit;
  text-align: inherit;
  appearance: none;
  outline: none;
  /* Reset button defaults */
  background: none;
  border: none;
}

/* Remove focus outline */
.dice-container:focus-visible {
  outline: none;
}

/* Disabled state */
.dice-container:disabled {
  cursor: default;
}

.dice-display {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-lg);
  align-items: center;
  justify-content: center;
  width: 100%;
}

.dice-section {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-xs);
  align-items: center;
}

.dice-label {
  font-size: 1rem;
  font-weight: 600;
  text-align: center;
}

.dice-row {
  display: flex;
  flex-direction: row;
  gap: var(--spacing-sm);
  align-items: center;
  justify-content: center;
}

.vs-divider {
  padding: var(--spacing-sm) 0;
  font-size: 1.5rem;
  font-weight: bold;
  color: #fff;
  text-shadow: 0 2px 4px rgb(0 0 0 / 50%);
}

.die {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 21rem;
  height: 21rem;
  font-size: 8.25rem;
  font-weight: bold;
  color: #1f2937;
  background: #fff;
  border: 9px solid #e5e7eb;
  border-radius: var(--border-radius-lg);
  box-shadow: 0 12px 36px rgb(0 0 0 / 15%);
  transition: all var(--transition-slow);
}





/* Battle controls */
.battle-controls {
  display: flex;
  flex-direction: column;
  grid-area: controls;
  gap: var(--spacing-sm);
  align-self: end;
  width: 100%;
  padding: var(--spacing-xs);
}

.result-message {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 2rem;
  padding: 0.75rem;
  text-align: center;
  background: rgb(255 255 255 / 95%);
  border-radius: var(--border-radius-md);
  box-shadow: 0 2px 8px rgb(0 0 0 / 10%);
  backdrop-filter: blur(2px);
}

.result-message p {
  font-size: 1rem;
  font-weight: 500;
  color: #1f2937;
}

.battle-stats {
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
  width: 100%;
  padding: 0.75rem;
  text-align: center;
  background: rgb(255 255 255 / 95%);
  border-radius: var(--border-radius-md);
  box-shadow: 0 2px 8px rgb(0 0 0 / 10%);
  backdrop-filter: blur(2px);
}

.battle-stats p {
  font-size: 0.875rem;
  color: #6b7280;
}

.battle-stats strong {
  color: #1f2937;
}
